(466, 432, '_form', '<p class="grid_12">1.Your Details:</p>\n\n<p class="grid_2">NAME<br />\n    [text your-name]\n</p> \n\n<p  class="grid_3">DOB<br />\n[date your-dob]\n</p>\n\n<p  class="grid_3">PHONE<br />\n[tel your-phone]\n</p>\n\n<p  class="grid_3">EMAIL<br />\n    [email your-email] </p>\n\n<p  class="grid_12">SEX<br />\n[select your-sex "MALE" "FEMALE"]\n</p>\n\n<p  class="grid_12">IF IN AUSTRALIA，WHAT IS YOUR VISA STATUS<br />\n[text your-visa]\n</p>\n\n<p class="grid_3" >IELTS(OR YOUR FORECASTING):AVERAGE <br />\n[text your-ielts]\n<p class="grid_2" >LISTEN[text your-ielts-listen]</p>\n<p class="grid_2" >SPEAK[text your-ielts-speak]</p>\n<p class="grid_2" >READ[text your-ielts-reading]</p>\n<p class="grid_2" >WRITE[text your-ielts-writing]</p>\n\n<p  class="grid_12">IELTS ATTEND DATE<br />\n[date your-ielts-date]\n</p>\n\n<p class="grid_12">2.YOUR DEGREE:</p>\n\n\n<p class="grid_5">HIGHEST DEGREE<br />\n[select your-degree "JUNIOR HIGH" "SECONDARY HIGH" "DIPLOMA" "BACHELOR" "MASTER" "DOCTOR"]\n</p>\n\n<p class="grid_5">YEAR<br />\n[select your-study-year "2 YEARS" "3 YEARS" "4 YEARS" "5 YEARS"]\n</p>\n\n<p  class="grid_3">HIGHEST DEGREE GRADUATE DATE<br />\n[date your-graduate-date]\n</p>\n\n<p  class="grid_4">HIGHEST DEGREE GRADUATE SCHOOL<br />\n[text your-graduate-school]\n</p>\n\n<p  class="grid_4">MAJOR<br />\n[text your-major]\n</p>\n\n<p class="grid_12">3.WORK EXPERIENCE:</p>\n\n<p class="grid_12">LATEST JOB<br />\n[textarea your-work-experience x10]\n</p>\n\n<p class="grid_5">LAST JOB START DATE\n[date your-start-date]\n</p>\n\n<p class="grid_5">LAST JOB END DATE\n[date your-finish-end]\n</p>\n\n<p class="grid_12">IF MORE THAN ONE JOB,YOUR MAY PROVIDE ADDITIONAL INFORMATION<br />\n[textarea your-more-experience x10]\n</p>\n\n<p class="grid_12">4.RELATIVES IN AUSTRALIA:</p>\n\n<p class="grid_12">IF HAVE RELATIVES IN AUSTRALIA, PLEASE GIVE DETAILS：<br />\n[textarea your-relatives x10]\n</p>\n\n<p class="grid_12">IS YOUR RELATIVE AUSTRALIA CITIZEN OR PERMANENT RESIDENT<br />\n[select your-relatives-visa "YES" "NO"]\n</p>\n\n<p class="grid_12">5.STUDY IN AUSTRALIA:</p>\n\n<p class="grid_12">FINISH MORE THAN TWO YEARS STUDY<br />\n[select study-in-au "YES" "NO"]\n</p>\n\n<p  class="grid_4">MAJOR<br />\n[text your-major-au]\n</p>\n\n<p  class="grid_4">STUDY FINISH DATE<br />\n[date your-graduate-year-au]\n</p>\n\n<p  class="grid_12">MORE DETAILS<br />\n[textarea your-other-status-au x10]\n</p>\n\n<p  class="grid_12">HOW DO YOU WANT US TO CONTACT YOU\n[select your-contact-way "EMAIL" "PHONE"]\n</p>\n\n<p  class="grid_3">[submit "SEND"]</p>\n<p  class="grid_3"><input type="reset" value="RESET" /></p>\n\n<br class="clear" />'),
