INSERT INTO `wp_huge_itslider_images` (`id`, `name`, `slider_id`, `description`, `image_url`, `sl_url`, `sl_type`, `link_target`, `ordering`, `published`, `published_in_sl_width`) VALUES
(2, '南平洋事务所针对投资相关法规及政府援助方桉为您投资澳洲提供建议。', '1', '<p>澳洲提供国际投资人各式各样的绝佳机会，从传统产业（如农业与矿业）到快速发展的产业（如资讯科技产业与生物科技产业）。\r\n<br />\r\n澳洲是安全的投资地点，是全球最具韧性的经济体之一。\r\n<br />\r\n澳洲的政治稳固、民主治理制度清廉、法规环境进步，赋予企业与市场最高度的信心与安全感</p>', 'http://localhost/southpac/wp-content/uploads/2014/06/slide31.jpg', '', 'image', 'on', 2, 1, NULL),
(3, '南平洋事务所为您移民澳洲量身定制移民计划和整套移民方案。', '1', '<p>澳大利亚地大物博，位居南半球地带，四季气候恰与地处北半球的中国相反。海岸线绵延总计三万七千公里，横跨南太平洋及印度洋。\r\n<br />\r\n澳洲全国人口共约一千九百万人，因提倡多元文化，加上其原住民，已吸纳世界各族裔同炉共治。</p>', 'http://localhost/southpac/wp-content/uploads/2014/06/slide11.jpg', '', 'image', 'on', 0, 1, NULL),
(5, '南平洋事务所为您留学澳洲提供全方面咨询和相关学校申请。', '1', '<p>留学资讯一站式服务：\r\n<br />\r\n含澳大利亚留学、澳大利亚签证、澳大利亚留学申请、澳大利亚留学条件、澳大利亚留学费用等信息</p>', 'http://localhost/southpac/wp-content/uploads/2014/06/slide21.jpg', '', '', 'on', 1, 2, 1);

