--
-- Table structure for table `wp_livelychatsupport_hours`
--

CREATE TABLE IF NOT EXISTS `wp_livelychatsupport_hours` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT,
  `day` varchar(20) DEFAULT NULL,
  `open_at` mediumint(9) DEFAULT NULL,
  `close_at` mediumint(9) DEFAULT NULL,
  `responder_id` mediumint(9) NOT NULL,
  `via` varchar(20) DEFAULT NULL,
  `created_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `updated_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `wp_livelychatsupport_messages`
--

CREATE TABLE IF NOT EXISTS `wp_livelychatsupport_messages` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT,
  `convo_token` varchar(32) NOT NULL,
  `body` text NOT NULL,
  `from_agent` tinyint(1) NOT NULL,
  `created_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `wp_livelychatsupport_surveys`
--

CREATE TABLE IF NOT EXISTS `wp_livelychatsupport_surveys` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT,
  `title` varchar(155) DEFAULT NULL,
  `urls` text NOT NULL,
  `delay` varchar(5) NOT NULL,
  `questions` text NOT NULL,
  `thanks` text,
  `created_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `updated_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `wp_livelychatsupport_triggers`
--

CREATE TABLE IF NOT EXISTS `wp_livelychatsupport_triggers` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT,
  `urls` text NOT NULL,
  `delay` varchar(5) NOT NULL,
  `body` text NOT NULL,
  `created_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `updated_at` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `wp_options`
--

CREATE TABLE IF NOT EXISTS `wp_options` (
  `option_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `option_name` varchar(64) NOT NULL DEFAULT '',
  `option_value` longtext NOT NULL,
  `autoload` varchar(20) NOT NULL DEFAULT 'yes',
  PRIMARY KEY (`option_id`),
  UNIQUE KEY `option_name` (`option_name`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1257 ;

